    <div>
      <h3>fetch</h3>
      <button>切换图片</button>
      <img src='' alt='' />
    </div>
  )
}

GetData.getInitialProps = async () => {
  const res = await axios.get('http://www.easy-mock.com/mock/5cfcce489dc7c36bd6da2c99/xiaojiejie/getList')
  console.log(res)

  return { url: '123' }
}


export default GetData
